mapper.select:
SELECT *, Floor(price * (1 - discount/100)) AS `salePrice` 
FROM `km_product` WHERE `cate1`=#{cate1} AND `cate2`=#{cate2}
ORDER BY `sold` DESC;
ORDER BY `price` ASC;
ORDER BY `price` DESC;
ORDER BY `score` ASC;
ORDER BY `review` ASC;
ORDER BY `rdate` ASC;

SELECT a.*, b.cate1, b.cate2, b.thumb1 
FROM `km_product_cart` AS a
JOIN `km_product` AS b 
ON a.code = b.code
WHERE uid = #{uid}
ORDER BY `seq` DESC;
